export const intialState = {
  user: null,
  playlists: [],
  playing: false,
  item: null,
  token:
    'BQDpqCJ6mlM8X288UQxFxkBPo89dyiKbag4PtrHcMM_HzcAXzVvxISV04Qp71lMfYRr6S3cexBiUu1L0d68S1GcUVp2xAx6Uax_P5f',
};

const reducer = (state, action) => {
  console.log(action);

  //Action -> type,[payload]
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.user,
      };
    case 'SET_TOKEN':
      return {
        ...state,
        token: action.token,
      };
    default:
      return state;
  }
};

export default reducer;
