export const intialState = {
  user: null,
  playlists: [],
  playing: false,
  item: null,
  token:
    'BQAMYk4nzgKrwOiQsojIYVd1pOAhSlqgtld1VNZe5vxpPkkjSoI6mK1l6y-Ubv7tL37PDnUtVhEfwWKmDBbetCmEMnsZFZs_3cSg6fl6I4v5AERCOKiHxFLgTV9luUiOweVHB00sx4_XNvc65ujr00ySM7W6V6M',
};

const reducer = (state, action) => {
  console.log(action);

  //Action -> type,[payload]
  switch (action.type) {
    case 'SET_USER':
      return {
        ...state,
        user: action.user,
      };
    case 'SET_TOKEN':
      return {
        ...state,
        token: action.token,
      };
    default:
      return state;
  }
};

export default reducer;
